package JavaBasics;

public class StringConcatination {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     
		// println - is used to print on the console with a new line
		// print - is used to print on the console on the same line
		int i =10;
		int j= 20;
		
		String s1 = "Hello";
		String s2 = "World";
		
		System.out.println(i+j);
		System.out.println(s1+s2);
		System.out.println(i+j+s1+s2);
		System.out.println(s1+s2+i+j);
		System.out.println(s1+s2+(i+j));
		System.out.println("the value of i is "+i);
		System.out.println("the value of i+j is "+(i+j));
		System.out.print("Hello World");
		System.out.println("Hello Selennium");

		
	}

}
